	import com.giantstone.security.TccrbSignature;   //包名
	import com.giantstone.tcrcb.util.CommUtils;
	import com.giantstone.cnaps2.messagebean.recv.req.*;
	import java.util.List;
	import java.util.ArrayList;
	import java.util.HashMap;
	import java.lang.Long;
	import java.math.BigDecimal;

	Map extendMap = target.get("extendMap");
	extendMap.put("dataTypeId","DT2001");
	List AcctChckSum = new ArrayList();
	List beps = sourceBean.BEPS_721_Out.ccti;
	for(BEPS_721_OutCcti map : beps){

	String TransactionNettingDate = map.TransactionNettingDate;
	String TransactionNettingRound = map.TransactionNettingRound;
	String MessageType = map.MessageType;
	String SendTotalCount = map.SendTotalCount;
	String SendTotalAmount = map.SendTotalAmount;
	String ReceiveTotalCount = map.ReceiveTotalCount;
	String ReceiveTotalAmount = map.ReceiveTotalAmount;
	String ProcessStatus = map.ProcessStatus;
		Map sumSend = new HashMap();
		sumSend.put("mT", MessageType);
		sumSend.put("txNetgDt", TransactionNettingDate);
		sumSend.put("txNetgRnd", TransactionNettingRound);
		sumSend.put("prcSts", ProcessStatus);
		sumSend.put("sndRcvTp", "SR00");
		sumSend.put("ccy",SendTotalAmount.substring(0,3));
		Long nb = Long.valueOf(SendTotalCount);
		sumSend.put("nb", nb);
		BigDecimal amt = new BigDecimal(SendTotalAmount.substring(3));
		sumSend.put("amt", amt);
		sumSend.put("sumTypeId","ST2001");
		sumSend.put("dimTypeId","DIMT201");

		Map sumReceive = new HashMap();
		sumReceive.put("mT", MessageType);
		sumReceive.put("txNetgDt", TransactionNettingDate);
		sumReceive.put("txNetgRnd", TransactionNettingRound);
		sumReceive.put("prcSts", ProcessStatus);
		sumReceive.put("sndRcvTp", "SR01");
		sumReceive.put("ccy",ReceiveTotalAmount.substring(0,3));
		Long nb1 = Long.valueOf(ReceiveTotalCount);
		sumReceive.put("nb", nb1);
		BigDecimal amt2 = new BigDecimal(ReceiveTotalAmount.substring(3));
		sumReceive.put("amt", amt2);
		sumReceive.put("sumTypeId","ST2001");
		sumReceive.put("dimTypeId","DIMT201");
				Long temp = new Long(0);
			if(nb.compareTo(temp)>0){
				AcctChckSum.add(sumSend);
			}
			if(nb1.compareTo(temp)>0){
				AcctChckSum.add(sumReceive);
			}

	}

List beps2 = sourceBean.BEPS_721_Out.ccti2;
for(BEPS_721_OutCcti2 map2 : beps2){
