message-bean-mapping.manual-mapping:


import com.giantstone.security.TccrbSignature;   //包名
import com.giantstone.tcrcb.util.CommUtils;
import com.giantstone.cnaps2.messagebean.recv.req.*;
import java.util.List;
import java.util.ArrayList;
import java.util.HashMap;
import java.lang.Integer;
import java.lang.Long;
import java.math.BigDecimal;

Map extendMap = target.get("extendMap");
extendMap.put("dataTypeId","DT1002");

List AcctChckSum = new ArrayList();
String  TotalNumber = sourceBean.HVPS_713_Out.Prttn.TotalNumber;
String  startNb = sourceBean.HVPS_713_Out.Prttn.StartNumber;
String  endNb = sourceBean.HVPS_713_Out.Prttn.EndNumber;
	if(TotalNumber==null||TotalNumber.length()==0){
		TotalNumber = "0";
	}
	if(startNb==null||startNb.length()==0){
		startNb = "0";
	}
	if(endNb==null||endNb.length()==0){
		endNb = "0";
	}

	int start = Integer.parseInt(startNb);
	int end = Integer.parseInt(endNb);
	int pageNb = end - start + 1 ;
	if(start==0&&end==0){
		pageNb = 0;
	}
	Long prttnTtlNb = Long.valueOf(TotalNumber);
	Long prttnRcvNb = Long.valueOf(String.valueOf(pageNb));
	extendMap.put("prttnTtlNb",prttnTtlNb);
	extendMap.put("prttnRcvNb",prttnRcvNb);

List details = sourceBean.HVPS_713_Out.Details;
for(HVPS_713_OutDetails detail : details){
	String MessageType = detail.MessageType;
	String TransactionTypeCode = detail.TransactionTypeCode;
	String SendReceiveType = detail.SendReceiveType;
	String ProcessStatus = detail.ProcessStatus;
	String NumberOfTransactions = detail.NumberOfTransactions;
	Map Sum = new HashMap();
	Sum.put("mT", MessageType);
	Sum.put("txTpCd", TransactionTypeCode);
	Sum.put("prcSts", ProcessStatus);
	Sum.put("sndRcvTp", SendReceiveType);	
	Long nb = Long.valueOf(NumberOfTransactions);
	Sum.put("nb", nb);
	Sum.put("dimTypeId","DIMT101");

	int i = 0;
	List AcctChckDetails = new ArrayList();
	List checkingDetails = detail.CheckingDetail;
	for(HVPS_713_OutDetailsCheckingDetail checkingDetail : checkingDetails){
		String OriginalMessageIdentification2 = checkingDetail.OrgnlGrpHdr2.OriginalMessageIdentification;
		String OriginalInstructingParty2 =checkingDetail.OrgnlGrpHdr2.OriginalInstructingParty;
		String OriginalMessageType2 = checkingDetail.OrgnlGrpHdr2.OriginalMessageType;

		String DebitCreditIdentification = checkingDetail.DebitCreditIdentification;
		String CurrencyType = checkingDetail.CurrencyType;
		String Amount = checkingDetail.Amount;
		Map temp = new HashMap();
		temp.put("transId",OriginalMessageIdentification2+":"+OriginalInstructingParty2+":"+OriginalMessageType2);
		BigDecimal amt = new BigDecimal(Amount);
		temp.put("amt",amt);
		temp.put("ccy",CurrencyType);
		temp.put("prcSts", ProcessStatus);
		temp.put("dbtCdtId",DebitCreditIdentification);
		if((!OriginalMessageType2.startsWith("hvps"))&&amt.compareTo(new BigDecimal(0.00))==0){
			temp.put("detailsTypeId","DTLT1002");
		}else{
			temp.put("detailsTypeId","DTLT1001");
		}

		if(i==0){
			if("RMB".equals(CurrencyType)){
				Sum.put("ccy", "CNY");
			} else {
				Sum.put("ccy", CurrencyType);
			}
			i++;
		}

		AcctChckDetails.add(temp);
	}
	Sum.put("AcctChckDetails",AcctChckDetails);
	AcctChckSum.add(Sum);
}
extendMap.put("AcctChckSum",AcctChckSum);
