	import com.giantstone.security.TccrbSignature;   //包名
	import com.giantstone.tcrcb.util.CommUtils;
	import com.giantstone.cnaps2.messagebean.recv.req.*;
	import java.util.List;
	import java.util.ArrayList;
	import java.util.HashMap;
	import java.lang.Long;
	import java.math.BigDecimal;

Map extendMap = target.get("extendMap");
extendMap.put("dataTypeId","DT1004");

List AcctChckSum = new ArrayList();

String  TotalNumber = sourceBean.HVPS_716_Out.Prttn.TotalNumber;
String  startNb = sourceBean.HVPS_716_Out.Prttn.StartNumber;
String  endNb = sourceBean.HVPS_716_Out.Prttn.EndNumber;
	if(TotalNumber==null||TotalNumber.length()==0){
		TotalNumber = "0";
	}
	if(startNb==null||startNb.length()==0){
		startNb = "0";
	}
	if(endNb==null||endNb.length()==0){
		endNb = "0";
	}

	int start = Integer.parseInt(startNb);
	int end = Integer.parseInt(endNb);
	int pageNb = end - start + 1 ;
	if(start==0&&end==0){
		pageNb = 0;
	}
	Long prttnTtlNb = Long.valueOf(TotalNumber);
	Long prttnRcvNb = Long.valueOf(String.valueOf(pageNb));
	extendMap.put("prttnTtlNb",prttnTtlNb);
	extendMap.put("prttnRcvNb",prttnRcvNb);

	Long tempZero = new Long(0);
List Details = sourceBean.HVPS_716_Out.Details;
for(HVPS_716_OutDetails detail : Details){
	String MessageType = detail.MessageType;
	String TransactionTypeCode = detail.TransactionTypeCode;
	String SendingNumberOfTransactions = detail.SendingNumberOfTransactions;
	String SendingControlSum = detail.SendingControlSum;
	String ReceivingNumberOfTransactions = detail.ReceivingNumberOfTransactions;
	String ReceivingControlSum = detail.ReceivingControlSum;

	Map sumSend = new HashMap();
	sumSend.put("mT", MessageType);
	sumSend.put("txTpCd", TransactionTypeCode);
	//sumSend.put("prcSts", "");
	sumSend.put("sndRcvTp", "SR00");
	sumSend.put("ccy",SendingControlSum.substring(0,3));
	Long nb = Long.valueOf(SendingNumberOfTransactions);
	sumSend.put("nb", nb);
	BigDecimal amt = new BigDecimal( SendingControlSum.substring(3));
	sumSend.put("amt", amt);
	sumSend.put("sumTypeId","ST1003");
	sumSend.put("dimTypeId","DIMT102");

	List AcctChckDetailsSend = new ArrayList();
	List CheckingDetailOfSending = detail.CheckingDetailOfSending;
		for(HVPS_716_OutDetailsCheckingDetailOfSending checkingDetailOfSending : CheckingDetailOfSending){
			String OriginalMessageIdentification = checkingDetailOfSending.OrgnlGrpHdr.OriginalMessageIdentification;
			String OriginalInstructingParty = checkingDetailOfSending.OrgnlGrpHdr.OriginalInstructingParty;
			String OriginalMessageType = checkingDetailOfSending.OrgnlGrpHdr.OriginalMessageType;
			String CurrencyType = checkingDetailOfSending.CurrencyType;
			String Amount = checkingDetailOfSending.Amount;
			String ProcessStatus = checkingDetailOfSending.ProcessStatus;
			Map temp = new HashMap();
			temp.put("transId",OriginalMessageIdentification+":"+OriginalInstructingParty+":"+OriginalMessageType);
			BigDecimal amt1 = new BigDecimal(Amount);
			temp.put("amt",amt1);
			temp.put("ccy",CurrencyType);
			temp.put("prcSts",ProcessStatus);
			temp.put("detailsTypeId","DTLT1001");
			AcctChckDetailsSend.add(temp);
}
	sumSend.put("AcctChckDetails",AcctChckDetailsSend);
	if(nb.compareTo(tempZero)>0){
	AcctChckSum.add(sumSend);
	}

	Map sumReceive = new HashMap();
	sumReceive.put("mT", MessageType);
	sumReceive.put("txTpCd", TransactionTypeCode);
	//sumReceive.put("prcSts", "");
	sumReceive.put("sndRcvTp", "SR01");
	sumReceive.put("ccy",ReceivingControlSum.substring(0,3));
	Long nb2 = Long.valueOf(ReceivingNumberOfTransactions);
	sumReceive.put("nb", nb2);
	BigDecimal amt2 = new BigDecimal(ReceivingControlSum.substring(3));
	sumReceive.put("amt", amt2);
	sumReceive.put("sumTypeId","ST1003");
	sumReceive.put("dimTypeId","DIMT102");

	List AcctChckDetailsRCV = new ArrayList();
	List CheckingDetailOfReceiving = detail.CheckingDetailOfReceiving;
	for(HVPS_716_OutDetailsCheckingDetailOfReceiving checkingDetailOfReceiving : CheckingDetailOfReceiving){
		String OriginalMessageIdentification2 = checkingDetailOfReceiving.OrgnlGrpHdr2.OriginalMessageIdentification;
		String OriginalInstructingParty2 = checkingDetailOfReceiving.OrgnlGrpHdr2.OriginalInstructingParty;
		String OriginalMessageType2 = checkingDetailOfReceiving.OrgnlGrpHdr2.OriginalMessageType;
		String DebitCreditIdentification = checkingDetailOfReceiving.DebitCreditIdentification;
		String CurrencyType2 = checkingDetailOfReceiving.CurrencyType2;
		String Amount2 = checkingDetailOfReceiving.Amount2;
		String ProcessStatus2 = checkingDetailOfReceiving.ProcessStatus2;
		Map temp = new HashMap();
			temp.put("transId",OriginalMessageIdentification2+":"+OriginalInstructingParty2+":"+OriginalMessageType2);
			BigDecimal amt3 = new BigDecimal(Amount2);
			temp.put("amt",amt3);
			temp.put("ccy",CurrencyType2);
			temp.put("prcSts",ProcessStatus2);
			temp.put("detailsTypeId","DTLT1001");
		AcctChckDetailsRCV.add(temp);
	}
	sumReceive.put("AcctChckDetails",AcctChckDetailsRCV);
	if(nb2.compareTo(tempZero)>0){
		AcctChckSum.add(sumReceive);
	}
}

extendMap.put("AcctChckSum",AcctChckSum);
